def process_step(context: dict):
    """
    Calculate the sum of numbers a and b from context, and return the result as
    a dictionary with key 'sum'.

    Args:
        context (dict): A dictionary containing keys 'a' and 'b'. Values may be
                        numbers or numeric strings.

    Returns:
        dict: {'sum': <a + b>}
    """
    if not isinstance(context, dict):
        raise TypeError("context must be a dict")

    def _to_number(x):
        if isinstance(x, (int, float)):
            return x
        if isinstance(x, str):
            s = x.strip()
            # Try int first for whole numbers, then float
            try:
                return int(s)
            except ValueError:
                try:
                    return float(s)
                except ValueError as e:
                    raise TypeError(f"Value '{x}' is not a valid number") from e
        raise TypeError(f"Unsupported type for numeric conversion: {type(x).__name__}")

    if 'a' not in context or 'b' not in context:
        raise KeyError("context must contain keys 'a' and 'b'")

    a = _to_number(context['a'])
    b = _to_number(context['b'])

    return {'sum': a + b}
